virgula: .string ", "
	.align 2
barn: .string "\n"
	.align 2
matriz: .space 144
	.align 2
tamanho: .word
	.text
main:
	li a7,4
	la a0, bemvindo
	ecall

menu_princ:
	li a7,4
	la a0, print_menu
	ecall
	li a7,4
	la a0, hello
	ecall
	li a7,5
	ecall
	la a2,matriz
	la a3,tamanho
	li t1,1
	li t2,2
	li t3,3
	li t4,4
	li t5,5
	li t6,6
	beq t1,a0,lemat
	beq t2,a0,printmat
	beq t3,a0,maiomat
	beq t4,a0,ordmat
	beq t5,a0,detmat
	beq t6,a0,final
	j menu_princ

lemat:
	li a7,4
	la a0, matrix_type
	ecall	
	li a7, 5
	ecall 	
	sw a0,0(a3)
	add t4,a0,zero
	add t3,t4,zero
	mul t1,t4,t3
	mv t3,zero
	j montamat
	
montamat:
	beq t1,t2,menu_princ
	addi t2,t3,1
	mv t3,t2
	la a0, entrada
	li a7, 4
	ecall
	li a7, 5
	ecall
	sb a0,0(a2)
	addi a2,a2,4
	j montamat

printmat:
	lw t1, 0(a3)
	mul t3,t1,t1
	addi t3,t3,2
	beq a1,t1,barran
	lb a0, 0(a2)
	li a7, 1
	ecall
	addi t2,t2,1
	beq t3,t2,menu_princ
	la a0, virgula
	li a7, 4
	ecall
	addi a2,a2,4
	addi a1,a1,1
	j printmat
	
barran:
	la a0,barn
	li a7,4
	ecall
	li a1,0
	j printmat

